// ƒ∞ki random r…ông se√ß…ôk
const randomColor1 = colors[Math.floor(Math.random() * colors.length)];
let randomColor2 = colors[Math.floor(Math.random() * colors.length)];

// ∆èg…ôr eyni r…ông d√º≈üs…ô, ba≈üqa r…ông se√ß…ôk
